create table if not exists {{ params.param_hr_core_dataset_name }}.bi_executive_vacancy (
requisition_sid int64 not null options(description="unique id of an lawson generated requisition.")
, reporting_period string not null options(description="this is the month and year that the data is valid for. data is refreshed on the 15th of each month")
, lawson_company_num int64 options(description="the number that identifies a company.a company represents a business or legal entity of an organization")
, requisition_num int64 options(description="unique id of an lawson generated requisition.")
, group_name string options(description="the name of the group the division rolls up to.")
, division_name string options(description="the name of the division the market rolls up to.")
, market_name string options(description="the name of the market the facility rolls up to.")
, coid_name string options(description="the name of the facility that the requisition is open for.")
, coid string options(description="the company identifier which uniquely identifies a facility to corporate and all other facilities.")
, company_code string options(description="part of the unique identifier that identifies the company with which a facility is affiliated for application processing purposes.")
, lob_code string options(description="three character code that identifies the line of business the process level is associated with.")
, hospital_category_code string options(description="it captures current level of facility")
, facility_street_address_1_text string options(description="address line 1 of the facility.")
, facility_street_address_2_text string options(description="address line 2 of the facility.")
, facility_city_name string options(description="city name of the facility.")
, facility_state_code string options(description="the state code is the abbreviated 2 character code associated with the address state for a person or organization.")
, facility_zip_code string options(description="the zip code is the 5 digit location identifier associated with the address for a person or organization.  the zip code of an address generally designates a postal area or region.")
, requisition_desc string options(description="description of an requisition")
, job_code string options(description="unique job code of an hr company.")
, job_code_desc string options(description="description of job code of an hr company")
, status_code string options(description="this represents the available code values for the status of the employee or applicant. ")
, requisition_open_date date options(description="date on which requisition is opened")
, requisition_closed_date date options(description="date on which requisition is closed")
, approval_date_1 date options(description="contains the ending effective date for the requisition approval.")
, approval_date_2 date options(description="contains the ending effective date for the requisition approval.")
, approval_date_3 date options(description="contains the ending effective date for the requisition approval.")
, approval_date_4 date options(description="contains the ending effective date for the requisition approval.")
, approval_date_5 date options(description="contains the ending effective date for the requisition approval.")
, last_approval_date date options(description="contains the ending effective date for the requisition approval.")
, days_open_num int64 options(description="the number of days since the last approval date when etl was run.")
, location_code string options(description="unique location codes of an employee working location facility.")
, process_level_code string options(description="unique code of an hr company facility or process level. this field is spaces for hr companyrecords")
, dept_code string options(description="this field contains value of department code of an company")
, source_system_code string not null options(description="a one character code indicating the specific source system from which the data originated.")
, dw_last_update_date_time datetime not null options(description="datetime of update or load of this record to the enterprise data warehouse.")
)
 cluster by requisition_sid, reporting_period
options(description="this table contains the open requisitions for executive positions determined by job code and requisition status. this table is refreshed monthly and used in tableau reporting.");
