CREATE TABLE IF NOT EXISTS {{ params.param_hr_core_dataset_name }}.employee_nurse_detail (employee_sid INT64 NOT NULL OPTIONS(description="Unique numeric number generated for unique combination of Employee Identifier and HR Company Identifier"), license_num_text STRING NOT NULL OPTIONS(description="The license number associated with certain code types."), report_date DATE OPTIONS(description="The date that the report was run and the record was received and loaded into the EDW. "), ncsbn_id NUMERIC(10,0) OPTIONS(description="Unique Identifier assigned to a nurse after licensure is granted."), employee_34_login_code STRING OPTIONS(description="The User Id field will be populated from the actual system user ID code when the user adds, changes, or deletes a record."), employee_num INT64 OPTIONS(description="Employee number of an employee associated with HR Lawson company"), lawson_company_num INT64 NOT NULL OPTIONS(description="This field will have employee associated legal lawson company number"), process_level_code STRING NOT NULL OPTIONS(description="Unique process level code of an HR company value maintained in this field."), source_system_code STRING NOT NULL OPTIONS(description="A one character code indicating the specific source system from which the data originated."), dw_last_update_date_time DATETIME NOT NULL OPTIONS(description="Timestamp of update or load of this record to the Enterprise Data Warehouse.")) CLUSTER BY Employee_SID, License_Num_TextOPTIONS(description="Contains employee information specific to employees with a job code related to nursing. Data sourced from Nursys. ");