CREATE TABLE  IF NOT EXISTS  {{ params.param_hr_core_dataset_name }}.employee_talent_profile
(
  employee_talent_profile_sid INT64 NOT NULL OPTIONS(description="Unique numeric number generated for unique combination of Employee Identifier and HR Company Identifier"),
  valid_from_date DATETIME NOT NULL OPTIONS(description="Effective start date of the record or load date."),
  employee_sid INT64 OPTIONS(description="Unique ETL Generated Number for an HCA Employee Number"),
  employee_first_name STRING OPTIONS(description="This field maintain employee first name"),
  employee_middle_name STRING OPTIONS(description="This field maintains employee middle name"),
  employee_last_name STRING OPTIONS(description="This field maintain employee last name"),
  employee_num INT64 NOT NULL OPTIONS(description="It is Lawson employee number assigned to each HR company employee"),
  employee_3_4_login_code STRING OPTIONS(description="This is employee network 3/4 id"),
  job_family_text STRING OPTIONS(description="Refers to how jobs are categorized.  Jobs are organized by the type of work performed, such as executive vs administrative.  The Lawson Job Code field correlates to the TMS Job Family field"),
  job_code STRING OPTIONS(description="Unique Job code of an HR Company."),
  job_code_sid INT64 OPTIONS(description="Unique Job code of an HR Company."),
  position_code_text STRING OPTIONS(description="The Lawson Position Number correlates to the TMS Job Code field.  This field in TMS is a concatenation of the Lawson HR Company code and the Lawson Position Number"),
  position_code STRING OPTIONS(description="This represents the user defined codes that represents a position in the HR company."),
  position_sid INT64 OPTIONS(description="Unique ETL generated sequence number for each user defined codes that represents a position in the HR company."),
  position_title_text STRING OPTIONS(description="Title of the position in TMS"),
  org_hierarchy_text STRING OPTIONS(description="This field Provides the organizations above an employees organization (vertical roll-up)"),
  manager_name STRING OPTIONS(description="This field  will maintain manager name"),
  manager_employee_num INT64 OPTIONS(description="Lawson employee ID for the employees manager"),
  travel_willingness_id INT64 OPTIONS(description="A unique identifier that indicates if an employee is willing to travel."),
  travel_willingness_pct_range_text STRING OPTIONS(description="This field provide employee travel percentage range in different bracket. Example 10 to 20 , 20 to 30 etc"),
  travel_location_text STRING OPTIONS(description="This field describes the employee prefered travel locations. This information is manually filled by employee."),
  relocation_willingness_id INT64 OPTIONS(description="A unique identifier that indicates if the employee is willing to relocate."),
  relocation_location_text STRING OPTIONS(description="This field describes the employee prefered relocation locations. This information is manually filled by employee."),
  calibrated_performance_rating_id INT64 OPTIONS(description="This is the employees final overall performance rating from the most recent review cycle.  The calibration takes place after the self/manager reviews and the 9-box ranking for an employee. "),
  overall_performance_rating_id INT64 OPTIONS(description="This is the employee overall performance rating from the most recent review cycle prior to calibration."),
  smry_competency_rating_id INT64 OPTIONS(description="This is the employee summary competency rating of all competencies from the most recent review cycle."),
  smry_goal_rating_id INT64 OPTIONS(description="This is the final summary goal rating given to an employee"),
  employee_promotability_interest_id INT64 OPTIONS(description="Interest level of the employee to be promoted (N;Not Interested, L:Low, M:Medium, H:High, A: Unknown). "),
  potential_performance_id INT64 OPTIONS(description="Indicates the employees potential performance and for advancement within the organization"),
  future_role_1_leadership_level_id INT64 OPTIONS(description="Indicates the next role that the employee would be a good fit for within a certain timeframe"),
  future_role_1_org_size_id INT64 OPTIONS(description="Indicates the organization size / scope for the Future 1 role. - A: Small - B: Medium - C: Large - D: Extra Large - E: Market - F: Division - G: Region - H: Group - I: Corp - N/A"),
  future_role_1_timeframe_id INT64 OPTIONS(description="Timeframe by which it is expected that the employee will be ready for the Future Role 1. - A: Immediate - B: Within 1 year - C: 1-2 years - D: 2-3 years - E: > 3 years - N/A"),
  future_role_1_role_id INT64 OPTIONS(description="Specifies the role and/or functional area for the employees Future 1 role"),
  future_role_2_leadership_level_id INT64 OPTIONS(description="Indicates the longer term role that the employee would eventually be a good fit for within a certain timeframe"),
  future_role_2_org_size_id INT64 OPTIONS(description="Indicates the organization size / scope for the Future 2 role. - A: Small - B: Medium - C: Large - D: Extra Large - E: Market - F: Division - G: Region - H: Group - I: Corp - N/A"),
  future_role_2_timeframe_id INT64 OPTIONS(description="Timeframe by which it is expected that the employee will be ready for the Future Role 2. - A: Immediate - B: Within 1 year - C: 1-2 years - D: 2-3 years - E: > 3 years - N/A"),
  future_role_2_role_id INT64 OPTIONS(description="Specifies the role and/or functional area for the employees Future 2 role"),
  flight_risk_probability_id INT64 OPTIONS(description="Code indicating the most likely driver for the employees flight risk."),
  flight_risk_timeframe_id INT64 OPTIONS(description="Indicates the likelihood that the employee will voluntarily leave his or her position"),
  flight_risk_driver_text STRING OPTIONS(description="Code indicating the most likely driver for the employees flight risk."),
  flight_risk_secondary_driver_text STRING OPTIONS(description="Code indicating the most likely driver for the employees flight risk."),
  valid_to_date DATETIME NOT NULL OPTIONS(description="Effective end date of the record"),
  lawson_company_num INT64 NOT NULL OPTIONS(description="The number that identifies a company.A company represents a business or legal entity of an organization"),
  process_level_code STRING NOT NULL OPTIONS(description="Unique process level code of an HR company value mainatined in this field."),
  calibration_session_created_date DATE OPTIONS(description="The date the Calibration Session was created"),
  calibration_session_name STRING OPTIONS(description="The name of the Calibration Session"),
  calibration_session_last_mod_date_time DATETIME OPTIONS(description="The last date the Calibration Session was modified"),
  calibration_session_published_date DATE OPTIONS(description="The date the Calibration Session was published to the Talent Profile"),
  calibration_session_created_by_name STRING OPTIONS(description="The name of the person who created the session"),
  calibration_box_id INT64 OPTIONS(description="The unique identifier for each Calibration Box."),
  company_tenure_text STRING OPTIONS(description="Text describing the length of time with the company"),
  position_tenure_text STRING OPTIONS(description="Text describing the length of time in a position"),
  source_system_code STRING NOT NULL OPTIONS(description="A one character code indicating the specific source system from which the data originated."),
  dw_last_update_date_time DATETIME NOT NULL OPTIONS(description="Timestamp of update or load of this record to the Enterprise Data Warehouse.")
)
CLUSTER BY employee_talent_profile_sid
OPTIONS(description="This table identifies the individual and also includes some of the information from the employees talent profile such as employee position,Travel and relocation preferences, Calibrated performance rating, Future role details, Potential rating.");