CREATE TABLE IF NOT EXISTS {{ params.param_hr_core_dataset_name }}.offer_status (offer_sid INT64 NOT NULL OPTIONS(description="A unique identifier for each offer."), valid_from_date DATETIME NOT NULL OPTIONS(description="Date on which the record became valid. Load date typically."), offer_status_id INT64 OPTIONS(description="A unique identifier fro each status an offer can have."), valid_to_date DATETIME OPTIONS(description="Date on which the record was invalidated."), source_system_code STRING NOT NULL OPTIONS(description="A one character code indicating the specific source system from which the data originated."), dw_last_update_date_time DATETIME NOT NULL OPTIONS(description="Timestamp of update or load of this record to the Enterprise Data Warehouse.")) CLUSTER BY Offer_SIDOPTIONS(description="Contains the status history of an offer.");