CREATE TABLE IF NOT EXISTS {{ params.param_hr_core_dataset_name }}.question_level_access (question_id STRING NOT NULL OPTIONS(description="Contains the id for the question being asking in the survey."), survey_category_num INT64 NOT NULL OPTIONS(description="This is the category number for the survey. Category number, code and category text define a unique Survey,"), survey_category_code STRING NOT NULL OPTIONS(description="Is the Category Code for the survey. Category number, code and category text define a unique Survey,"), survey_sub_category_text STRING OPTIONS(description="This defines the sub category of survey."), question_desc STRING OPTIONS(description="Contains the text of the question being asked."), question_type_desc STRING OPTIONS(description="Description of question type code"), question_short_name STRING OPTIONS(description="This defines a short description for the survey question."), lob_name STRING OPTIONS(description="Name of the line of business."), source_system_code STRING NOT NULL OPTIONS(description="A one character code indicating the specific source system from which the data originated."), dw_last_update_date_time DATETIME NOT NULL OPTIONS(description="Timestamp of update or load of this record to the Enterprise Data Warehouse.")) CLUSTER BY Question_Id, Survey_Category_Num, Survey_Category_CodeOPTIONS(description="Contains the Question Ids and descriptions for specific questions that a line of business needs access to.");