CREATE TABLE IF NOT EXISTS {{ params.param_hr_core_dataset_name }}.ref_performance_rating_box_score (performance_rating_id INT64 NOT NULL OPTIONS(description="This field maintains list of ratings given by manager to empoyee or employee to self."), performance_potential_id INT64 NOT NULL OPTIONS(description="This is the unique sequence number generated for each potential description mainatained in performance management system"), overall_performance_rating_desc STRING OPTIONS(description="The overall rating description coming from TMS."), performance_potential_desc STRING OPTIONS(description="The potential performance description coming from TMS."), box_score_num INT64 OPTIONS(description="A numeric value derived from the rating and potential TMS descriptions."), box_score_desc STRING OPTIONS(description="A description derived from the rating and potential TMS descriptions."), box_score_group_desc STRING OPTIONS(description="A descriptive bucket for each box score."), source_system_code STRING NOT NULL OPTIONS(description="A one character code indicating the specific source system from which the data originated."), dw_last_update_date_time DATETIME NOT NULL OPTIONS(description="Timestamp of update or load of this record to the Enterprise Data Warehouse.")) CLUSTER BY Performance_Rating_Id, Performance_Potential_IdOPTIONS(description="Contains a box score for each set up rating criteria in Authoria for a given employee. Manually maintained by an HR resource.");