CREATE TABLE IF NOT EXISTS {{ params.param_hr_core_dataset_name }}.ref_recruiting_source (recruiting_source_code STRING NOT NULL OPTIONS(description="A unique code for each recruiting source."), recruiting_source_name STRING OPTIONS(description="The name of the recruiting source."), recruiting_source_type_desc STRING OPTIONS(description="The type of recruiting source."), recruiting_availability_ind STRING OPTIONS(description="Indicates if it is available to use."), source_system_code STRING NOT NULL OPTIONS(description="A one character code indicating the specific source system from which the data originated."), dw_last_update_date_time DATETIME NOT NULL OPTIONS(description="Timestamp of update or load of this record to the Enterprise Data Warehouse.")) CLUSTER BY Recruiting_Source_CodeOPTIONS(description="Contains reference data pertaining to recruiting sources used by HR such newletters, websites, job boards, etc.");