CREATE TABLE IF NOT EXISTS {{ params.param_hr_core_dataset_name }}.ref_recruitment_location (location_num NUMERIC NOT NULL OPTIONS(description="A unique identifier for each location."), level_num INT64 OPTIONS(description="A number that identifies the granularity of a location."), location_name STRING OPTIONS(description="The name of the location."), location_code_text STRING OPTIONS(description="A code for each location."), work_location_code_text STRING OPTIONS(description="A work code for each location."), addr_sid INT64 OPTIONS(description="This field is ETL generated unique sequence number for each business or Individual address."), source_system_code STRING NOT NULL OPTIONS(description="A one character code indicating the specific source system from which the data originated."), dw_last_update_date_time DATETIME NOT NULL OPTIONS(description="Timestamp of update or load of this record to the Enterprise Data Warehouse.")) CLUSTER BY Location_NumOPTIONS(description="Contains different locations of all entities - candidates, jobs, facilities.");