CREATE TABLE IF NOT EXISTS {{ params.param_hr_core_dataset_name }}.ref_skill_mix (skill_mix_job_code STRING NOT NULL OPTIONS(description="This field maintains first two digits of a job code."), skill_mix_desc STRING OPTIONS(description="This field maintains skillmix description"), source_system_code STRING NOT NULL OPTIONS(description="A one character code indicating the specific source system from which the data originated."), dw_last_update_date_time DATETIME NOT NULL OPTIONS(description="Timestamp of update or load of this record to the Enterprise Data Warehouse.")) CLUSTER BY Skill_Mix_Job_CodeOPTIONS(description="This table mainatins available skillmix codes across all Lawson HR company for the first two characters of a job code.");