CREATE TABLE IF NOT EXISTS {{ params.param_hr_core_dataset_name }}.ref_survey_form (survey_form_text STRING NOT NULL OPTIONS(description="This is the survey form text identifier"), survey_form_name STRING OPTIONS(description="Description for the Survey Form"), source_system_code STRING NOT NULL OPTIONS(description="A one character code indicating the specific source system from which the data originated."), dw_last_update_date_time DATETIME NOT NULL OPTIONS(description="Timestamp of update or load of this record to the Enterprise Data Warehouse.")) CLUSTER BY Survey_Form_TextOPTIONS(description="This reference table stores the descriptions for the Survey forms.");