SELECT (CASE WHEN hcfacility.hcfapasid = 'CAPELLA' OR hcfacility.hcfapasid = 'LIFEPOINT-NAS' THEN 'CLIENT-NAS' WHEN hcfacility.hcfapasid = 'LIFEPOINT-OPK' THEN 'CLIENT-OPK' WHEN hcfacility.hcfapasid = 'LIFEPOINT-RIC' THEN 'CLIENT-RIC' WHEN hcfacility.hcfapasid = 'DALLAS' THEN 'DAL/SAN' WHEN hcfacility.hcfapasid = 'SANANTONIO' THEN 'DAL/SAN' ELSE hcfacility.hcfapasid END) AS csuaafapasid, (CASE WHEN hcfacility.hcfapasid = 'CAPELLA' OR hcfacility.hcfapasid = 'CLIENT-NAS' OR hcfacility.hcfapasid = 'LIFEPOINT-NAS' THEN 'NASHVILLE' WHEN hcfacility.hcfapasid = 'CLIENT-OPK' OR hcfacility.hcfapasid = 'LIFEPOINT-OPK' THEN 'ORANGEPARK' WHEN hcfacility.hcfapasid = 'CLIENT-RIC' OR hcfacility.hcfapasid = 'LIFEPOINT-RIC' THEN 'RICHMOND' WHEN hcfacility.hcfapasid = 'DALLAS' THEN 'DAL/SAN' WHEN hcfacility.hcfapasid = 'SANANTONIO' THEN 'DAL/SAN' ELSE hcfacility.hcfapasid END) AS ssc, stuseraudit.csuamonthlyreviewid, stuseraudit.stuamonth, stuseraudit.stuayear, stuseraudit.hcuareviewperiod, stuseraudit.stuarvwstart, stuseraudit.stuarvwend, stuserauditacct.stuaauserid, stuseraudit.stuaid, stuserauditacct.stuaaauditid, stuserauditacct.stuaaid, stuserauditacct.csuaastatus, stuserauditacct.csuaafahospnum AS unit, stuserauditacct.csuaaenptacct AS enpatacct, stuserauditacct.csuaapyiplan AS iplan, hcpayer.hcpyfinclass AS hcpyfinclass, stuserauditacct.stuaaearnedpoints, stuserauditacct.stuaapossiblepoints, round(stuserauditacct.stuaascore, 2) AS stuaascore, stuserauditacct.stuaacreatedate, stuserauditacct.stuaacompleteflag, stuserauditacct.stuaacompletedate FROM ((sqluser.stuserauditacct stuserauditacct LEFT OUTER JOIN sqluser.stuseraudit stuseraudit ON stuserauditacct.stuaaauditid = stuseraudit.stuaid) LEFT OUTER JOIN sqluser.hcpayer hcpayer ON stuserauditacct.csuaapayerid = hcpayer.hcpyid), sqluser.hcfacility hcfacility WHERE stuserauditacct.csuaafahospnum = hcfacility.csfahospnum AND stuseraudit.stuarvwend >= '2020-11-01' ORDER BY stuserauditacct.stuaaauditid