SELECT f.hcfapasid AS ssc, 'AR1' AS artivainstancecode, l.hclhencntrid AS encounter_id, f.csfahospnum AS unit_num, e.hcenptacct AS patacct, a.hcacseqnum AS ins_iplan_order_num, a.csaciplan AS iplan_id, l.hclhacct AS liabilty_number, l.hclhuserid AS user_desc, l.hclhdate AS letter_sent_date, l.hclhtime AS letter_sent_time, l.hclhid AS letter_id, l.hclhltrid AS letter_code, l.hclhltrdesc AS letter_desc, l.cslhltrdetyn AS free_form_ind, l.hclhaction AS ltr_action, l.cslhtrackno AS tracking_num, l.hclhbal AS acct_balance, 'v_currtimestamp' AS dw_last_update_date_time FROM sqluser.hcencounter e LEFT OUTER JOIN sqluser.hcfacility f ON f.hcfaid = e.hcenfacility INNER JOIN sqluser.hcltrhist l ON l.hclhencntrid = e.hcenid LEFT OUTER JOIN sqluser.hcaccount a ON a.hcacencntrid = e.hcenid AND a.hcacldacct = l.hclhacct