SELECT sqluser.hcfacility.csfahospnumber AS unit_number, sqluser.hcencounter.hcenptacct AS pat_acct_num, sqluser.hcactionhist.hcacthuserid AS action_code_user, sqluser.hcactionhist.hcacthresid AS result_code, sqluser.hcactionhist.hcacthtim AS action_code_time, sqluser.hcactionhist.hcacthdte AS action_code_date, {fn right(sqluser.hcactionhist.hcactpayerid, 5)} AS iplan_id, sqluser.hcactionhist.hcacthstat AS status_code, sqluser.hcactionhist.csacthpool AS pool, sqluser.hcactionhist.hcacthactid AS action_code, sqluser.hcactionhist.hcacthid FROM sqluser.hcactionhist JOIN sqluser.hcencounter ON sqluser.hcencounter.hcenid = sqluser.hcactionhist.hcactencntrid JOIN sqluser.hcfacility ON sqluser.hcfacility.hcfaid = sqluser.hcencounter.hcenfacility JOIN sqluser.hcpas ON sqluser.hcpas.hcpsid = sqluser.hcfacility.hcfapasid LEFT JOIN sqluser.hcaccount ON sqluser.hcaccount.hcacid = sqluser.hcactionhist.hcacthacnum WHERE sqluser.hcactionhist.hcacthuserid <> 'CONVERT' AND sqluser.hcactionhist.hcacthuserid <> 'IMPORT' AND sqluser.hcactionhist.hcacthuserid <> 'sys' AND sqluser.hcactionhist.hcacthuserid <> 'SYS' AND sqluser.hcactionhist.hcacthuserid <> 'ARTSCHED' AND sqluser.hcactionhist.hcacthuserid <> 'ESSCBOT'