SELECT (CASE WHEN hcfacility.hcfapasid = 'CAPELLA' THEN REPLACE((hcfacility.hcfapasid), 'CAPELLA', 'CLIENT-NAS') WHEN hcfacility.hcfapasid LIKE '%LIFEPOINT%' THEN REPLACE((hcfacility.hcfapasid), 'LIFEPOINT', 'CLIENT') WHEN hcfacility.hcfapasid = 'DALLAS' THEN 'DAL/SAN' WHEN hcfacility.hcfapasid = 'SANANTONIO' THEN 'DAL/SAN' ELSE hcfacility.hcfapasid END) AS pasid, (CASE WHEN hcfacility.hcfapasid = 'CAPELLA' OR hcfacility.hcfapasid = 'CLIENT-NAS' OR hcfacility.hcfapasid = 'LIFEPOINT-NAS' THEN 'NASHVILLE' WHEN hcfacility.hcfapasid = 'CLIENT-OPK' OR hcfacility.hcfapasid = 'LIFEPOINT-OPK' THEN 'ORANGEPARK' WHEN hcfacility.hcfapasid = 'CLIENT-RIC' OR hcfacility.hcfapasid = 'LIFEPOINT-RIC' THEN 'RICHMOND' WHEN hcfacility.hcfapasid = 'DALLAS' OR hcfacility.hcfapasid = 'SANANTONIO' THEN 'DAL/SAN' ELSE hcfacility.hcfapasid END) AS ssc, stuseraudit.csuamonthlyreviewid, stuseraudit.stuamonth, stuseraudit.stuayear, stuseraudit.stuarvwend, stuseraudit.stuaphase, stuserauditacct.stuaaauditid, stuserauditacct.stuaaid, stuserauditsum.stusaresid, stuserauditsum.stusasort, stuserauditsum.stusadate, stuserauditsum.stusafield, stuserauditsum.stusaguideid, UPPER(stguidepage.stgdpgcaption) AS stgdpgcaption, stuserauditsum.stusaid, (CASE WHEN LEN(stuserauditsum.stusalabel) > 109 THEN LEFT(stuserauditsum.stusalabel, 109) ELSE stuserauditsum.stusalabel END) AS stusalabel, stuserauditsum.stusavalue, (CASE WHEN LEN(stuserauditsum.csusacomment) > 250 THEN REPLACE(REPLACE((LEFT(stuserauditsum.csusacomment, 250)), CHAR(10), '|'), CHAR(13), '|') ELSE REPLACE(REPLACE(stuserauditsum.csusacomment, CHAR(10), '|'), CHAR(13), '|') END) AS csusacomment FROM (((sqluser.stuseraudit stuseraudit LEFT OUTER JOIN sqluser.stuserauditacct stuserauditacct ON stuseraudit.stuaid=stuserauditacct.stuaaauditid) LEFT OUTER JOIN sqluser.stuserauditsum stuserauditsum ON stuserauditacct.stuaaid = stuserauditsum.stusaresid) LEFT OUTER JOIN sqluser.stguidepage ON stuserauditsum.stusapageid = stguidepage.stgdpgid), sqluser.hcfacility hcfacility WHERE stuserauditacct.csuaafahospnum = hcfacility.csfahospnum AND stusavalue = 'N' AND stuseraudit.stuarvwend >= '2020-11-01' ORDER BY stuserauditacct.stuaaauditid ASC, stuserauditsum.stusaresid ASC, stuserauditsum.stusasort ASC