SELECT (CASE WHEN hcfacility.hcfapasid = 'CAPELLA' THEN REPLACE((hcfacility.hcfapasid), 'CAPELLA', 'CLIENT-NAS') WHEN hcfacility.hcfapasid LIKE '%LIFEPOINT%' THEN REPLACE((hcfacility.hcfapasid), 'LIFEPOINT', 'CLIENT') WHEN hcfacility.hcfapasid = 'DALLAS' THEN 'DAL/SAN' WHEN hcfacility.hcfapasid = 'SANANTONIO' THEN 'DAL/SAN' ELSE hcfacility.hcfapasid END) AS pasid, (CASE WHEN hcfacility.hcfapasid = 'CAPELLA' OR hcfacility.hcfapasid = 'CLIENT-NAS' OR hcfacility.hcfapasid = 'LIFEPOINT-NAS' THEN 'NASHVILLE' WHEN hcfacility.hcfapasid = 'CLIENT-OPK' OR hcfacility.hcfapasid = 'LIFEPOINT-OPK' THEN 'ORANGEPARK' WHEN hcfacility.hcfapasid = 'CLIENT-RIC' OR hcfacility.hcfapasid = 'LIFEPOINT-RIC' THEN 'RICHMOND' WHEN hcfacility.hcfapasid = 'DALLAS' OR hcfacility.hcfapasid = 'SANANTONIO' THEN 'DAL/SAN' ELSE hcfacility.hcfapasid END) AS ssc, stuseraudit.csuamonthlyreviewid, stuseraudit.stuamonth, stuseraudit.stuayear, stuseraudit.stuarvwend, stuseraudit.stuaphase, stuseraudit.stuaid, stuserauditacct.stuaaid, stuser_agent.hcuadept, stuseraudit.stuauserid, stuser_agent.uafullname, stuseraudit.csuareviewer, stuseraudit.stuasupervisorid, stuserauditacct.csuaafahospnum, stuserauditacct.csuaaenptacct, stuserauditacct.csuaapyiplan, stuserauditacct.csuaoptoutreason, stuserauditacct.csuaaoptoutwfpath, (CASE WHEN LEN(stuserauditacct.csuaaoptoutcomment) > 150 THEN REPLACE(REPLACE((LEFT(stuserauditacct.csuaaoptoutcomment, 150)), CHAR(10), ''), CHAR(13), '') ELSE REPLACE(REPLACE(stuserauditacct.csuaaoptoutcomment, CHAR(10), ''), CHAR(13), '') END) AS optoutcomment FROM ((sqluser.stuserauditacct stuserauditacct LEFT OUTER JOIN sqluser.stuseraudit stuseraudit ON stuserauditacct.stuaaauditid = stuseraudit.stuaid) LEFT OUTER JOIN sqluser.stuser stuser_agent ON stuseraudit.stuauserid = stuser_agent.userid), sqluser.hcfacility hcfacility WHERE stuserauditacct.csuaafahospnum = hcfacility.csfahospnum AND stuserauditacct.csuaoptoutreason IS NOT NULL AND stuseraudit.stuarvwend>={d '2020-11-01'} ORDER BY stuserauditacct.stuaaid ASC