SELECT (CASE WHEN stuser.csuaaccess1 = 'CLIENTCOLL' THEN 'CLIENT-NSFW' ELSE 'NASHWEST' END) AS pasid, 'NASHWEST' AS csuassc, stuseraudit.csuamonthlyreviewid, stuseraudit.stuamonth, stuseraudit.stuayear, stuseraudit.hcuareviewperiod, stuseraudit.stuarvwstart, stuseraudit.stuarvwend, stuseraudit.stuaid, stuseraudit.stuauserid, stuser.uafullname, (CASE WHEN sum(stuserauditacct.stuaaearnedpoints) IS NULL THEN 0 ELSE sum(stuserauditacct.stuaaearnedpoints) END) AS auditearnedpoints, (CASE WHEN sum(stuserauditacct.stuaapossiblepoints) IS NULL THEN 0 ELSE sum(stuserauditacct.stuaapossiblepoints) END) AS auditpossiblepoints, stuseraudit.stuaphase, (CASE WHEN stuseraudit.csuastatus IS NULL THEN 'PENDING' ELSE stuseraudit.csuastatus END) AS csuastatus, stuseraudit.csuauserresponse, NULL AS prior1_week, NULL AS prior1_score, NULL AS prior2_week, NULL AS prior2_score, NULL AS prior3_week, NULL AS prior3_score, NULL AS prior4_week, NULL AS prior4_score, stuseraudit.csuascore, stuseraudit.csuagoalpercent, NULL AS currweekstrat, stuseraudit.csuareviewer, reviewer.uafullname AS reviewer, stuseraudit.stuasupervisorid, supervisor.uafullname AS supervisor, stuseraudit.csuareviewmtgdte FROM ((((sqluser.stuseraudit stuseraudit LEFT OUTER JOIN sqluser.stuserauditacct stuserauditacct ON stuseraudit.stuaid = stuserauditacct.stuaaauditid) LEFT OUTER JOIN sqluser.stuser stuser ON stuseraudit.stuauserid = stuser.userid) LEFT OUTER JOIN sqluser.stuser reviewer ON stuseraudit.csuareviewer = reviewer.userid) LEFT OUTER JOIN sqluser.stuser supervisor ON stuseraudit.stuasupervisorid = supervisor.userid) WHERE stuseraudit.stuarvwend >= '2020-11-01' AND LEN(stuseraudit.stuauserid) = 7 GROUP BY stuseraudit.stuauserid, stuseraudit.stuaid