SELECT geneticstestingfactid, corerecordid, patientdimid, tumortypedimid, diagnosisresultid, diagnosisdimid, facilitydimid, coid, navigatordimid, geneticsdate, REPLACE(REPLACE(LTRIM(RTRIM(geneticstesttype)), nCHAR(8211), nCHAR(32)), nCHAR(8217), nCHAR(32)) AS geneticstesttype, REPLACE(REPLACE(LTRIM(RTRIM(geneticsspecialist)), nCHAR(8211), nCHAR(32)), nCHAR(8217), nCHAR(32)) AS geneticsspecialist, REPLACE(REPLACE(LTRIM(RTRIM(geneticsbrcatype)), nCHAR(8211), nCHAR(32)), nCHAR(8217), nCHAR(32)) AS geneticsbrcatype, REPLACE(REPLACE(LTRIM(RTRIM(CAST(geneticscomments AS VARCHAR(1000)))), nCHAR(8211), nCHAR(32)), nCHAR(8217), nCHAR(32)) AS geneticscomments, REPLACE(REPLACE(LTRIM(RTRIM(concat('0x', CONVERT(VARCHAR(50), hbsource, 2)))), nCHAR(8211), nCHAR(32)), nCHAR(8217), nCHAR(32)) AS hbsource, 'v_currtimestamp' AS dw_last_update_date_time FROM navadhoc.dbo.patientgenetictesting (nolock);