SELECT src.physician_id AS physician_id, REPLACE(REPLACE(LTRIM(RTRIM(src.physician_name)), nCHAR(8211), nCHAR(32)), nCHAR(8217), nCHAR(32)) AS physician_name, LTRIM(RTRIM(src.physician_phone_num)) AS physician_phone_num, 'v_currtimestamp' AS dw_last_update_date_time FROM ( SELECT NULL AS physician_id, gynecologist AS physician_name, gynecologistphone AS physician_phone_num FROM navadhoc.dbo.dimpatient UNION SELECT NULL AS physician_id, primarycarephysician AS physician_name, pcpphone AS physician_phone_num FROM navadhoc.dbo.dimpatient UNION SELECT NULL AS physician_id, endtreatmentphysician, NULL AS physician_phone_num FROM navadhoc.dbo.patienttumor UNION SELECT medicalspecialistdimid AS physician_id, specialistname AS physician_name, NULL AS physician_phone_num FROM navadhoc.dbo.dimmedicalspecialist UNION SELECT NULL AS physician_id, hematologist, NULL AS physician_phone_num FROM navadhoc.dbo.patientheme ) src WHERE (LTRIM(RTRIM(src.physician_name)) IS NOT NULL OR LTRIM(RTRIM(src.physician_phone_num)) IS NOT NULL) AND (LTRIM(RTRIM(src.physician_name)) <> '' OR LTRIM(RTRIM(src.physician_phone_num)) <>'');