SELECT claim_id, code_seq_num, code_type_id, code_value, code_amt, CAST(code_from_dt AS DATE) AS code_from_dt, CAST(code_thru_dt AS DATE) AS code_thru_dt, (CASE WHEN code_poa_ind = 'Y' THEN 'Y' WHEN code_poa_ind = 'N' THEN 'N' WHEN code_poa_ind='U' THEN 'U' ELSE NULL END) AS code_poa_ind, NULL AS dw_last_update_date_time, NULL AS source_system_code FROM claimsconnectdb.dbo.fact_code WITH (nolock)