SELECT 'A' AS STACK, glx.co_id, glx.pat_no, glx.entered_ts, glx.gl_src, glx.gl_proc_type, glx.gl_account, glx.gl_proc_amt, glx.gl_serv_dt, glx.proc_code, glx.gl_source, glx.factor, glx.pat_type, glx.dept, glx.fin_class, glx.gl_adjust_ind, glx.foreign_fac_id, glx.foreign_dept, glx.foreign_pat_type, glx.foreign_gl_com, glx.foreign_fin_class, glx.process_ind, glx.pedate, acc.sub_unit_no, 'v_currtimestamp' AS dw_last_update_date_time FROM a.brbglx AS glx LEFT OUTER JOIN a.brbacc AS acc ON glx.stack = acc.stack AND glx.co_id = acc.co_id AND glx.pat_no = acc.pat_no WHERE date(glx.entered_ts) =date(CURRENT TIMESTAMP) -1 DAY